mapper.select:
select a.* from user_basics a right JOIN user_information b on a.user_basics_id=b.user_basics_id left join user_integral c on c.user_basics_id=b.user_basics_id where 1=1
and a.user_grade_id=#{user_grade_id,jdbcType=INTEGER}
and c.integral_basics>=#{integral_basics,jdbcType=INTEGER}

SELECT b.user_basics_id,m.information_compellation,m.information_card,g.grade_name,g.grade_nickname, i.integral_basics,i.integral_travel,i.integral_bonus,w.weixin_nickname, m.co_user_basics_id,ui.information_compellation as information_compellation3, m.user_resources,uin.information_compellation as superior_information_compellation, r.recommend_superior,b.user_grade_id FROM user_basics b LEFT JOIN user_integral i ON(b.user_basics_id=i.user_basics_id) LEFT JOIN user_grade g ON(b.user_grade_id=g.user_grade_id) LEFT JOIN user_information m ON(b.user_basics_id=m.user_basics_id) LEFT JOIN user_weixin w ON(b.user_basics_id=w.user_basics_id) LEFT JOIN user_information ui ON(ui.user_basics_id=m.co_user_basics_id)
LEFT JOIN user_recommend r ON(b.user_basics_id=r.user_basics_id) LEFT JOIN user_information uin on(r.recommend_superior=uin.user_basics_id) where 1=1
or m.information_compellation LIKE concat('%',#{information_compellation,jdbcType=VARCHAR},'%')
or m.information_card =#{information_card,jdbcType=VARCHAR}
OR m.user_basics_id = #{user_basics_id,jdbcType=INTEGER}
OR m.co_user_basics_id = #{co_user_basics_id,jdbcType=INTEGER}
order by m.user_basics_id desc

select count(*) from user_basics u1 left join user_integral u2 on
u1.user_basics_id =u2.user_basics_id
where 1=1

	and u1.user_grade_id=#{user_grade_id,jdbcType=INTEGER}


	and
	u2.integral_basics>=#{integral_basics,jdbcType=INTEGER}

