mapper.select:
SELECT bonus_extract_apply_id,user_basics_id,apply_add_date,apply_edit_date, edit_user_basics_id,apply_sum,apply_status,apply_postscript,receive_bank_card, receive_name FROM bonus_extract_apply
bonus_extract_apply_id=#{bonus_extract_apply_id,jdbcType=INTEGER}
AND user_basics_id=#{user_basics_id,jdbcType=INTEGER} and apply_status=1 OR user_basics_id=#{user_basics_id,jdbcType=INTEGER} and apply_status=3 OR user_basics_id=#{user_basics_id,jdbcType=INTEGER} and apply_status=2
AND apply_status=#{apply_status,jdbcType=INTEGER}
AND apply_add_date >= #{apply_add_date,jdbcType=BIGINT} AND apply_add_date <= #{apply_add_date1,jdbcType=BIGINT} AND apply_status=1
order by apply_add_date desc

SELECT a.bonus_extract_apply_id,a.user_basics_id,a.apply_add_date,a.apply_edit_date, a.edit_user_basics_id,a.apply_sum,a.apply_status,a.apply_postscript,a.receive_bank_card, a.receive_name,b.information_compellation FROM bonus_extract_apply a left join user_information b on a.user_basics_id=b.user_basics_id
bonus_extract_apply_id=#{bonus_extract_apply_id,jdbcType=INTEGER}
AND a.user_basics_id=#{user_basics_id,jdbcType=INTEGER} and apply_status=3 OR a.user_basics_id=#{user_basics_id,jdbcType=INTEGER} and apply_status=4 OR a.user_basics_id=#{user_basics_id,jdbcType=INTEGER} and apply_status=7
AND a.apply_status=#{apply_status,jdbcType=INTEGER}
AND a.apply_edit_date >= #{apply_edit_date2,jdbcType=BIGINT} AND apply_edit_date<#{apply_edit_date1,jdbcType=BIGINT} AND apply_status=3 OR a.apply_edit_date >= #{apply_edit_date2,jdbcType=BIGINT} AND apply_edit_date<#{apply_edit_date1,jdbcType=BIGINT} AND apply_status=4 OR a.apply_edit_date >= #{apply_edit_date2,jdbcType=BIGINT} AND apply_edit_date<#{apply_edit_date1,jdbcType=BIGINT} AND apply_status=7
order by apply_add_date desc

SELECT
bonus_extract_apply_id,user_basics_id,apply_add_date,apply_edit_date,
edit_user_basics_id,apply_sum,apply_status,apply_postscript,receive_bank_card,
receive_name
FROM bonus_extract_apply

	
		
		    user_basics_id=#{user_basics_id,jdbcType=INTEGER}
		
		
		    AND apply_status=#{apply_status,jdbcType=INTEGER}
		
		
		   
		    AND apply_edit_date  >=  #{apply_edit_date,jdbcType=BIGINT} AND apply_edit_date  <=  #{datetime2,jdbcType=BIGINT} 
		   
		
	
	order by apply_edit_date desc

SELECT
bonus_extract_apply_id,user_basics_id,apply_add_date,apply_edit_date,
edit_user_basics_id,apply_sum,apply_status,apply_postscript,receive_bank_card,
receive_name
FROM bonus_extract_apply

	
		
		    user_basics_id=#{user_basics_id,jdbcType=INTEGER}
		
		
		    AND apply_status=#{apply_status,jdbcType=INTEGER}
		
		
		   
		    AND apply_add_date  >=  #{apply_add_date,jdbcType=BIGINT} AND apply_add_date  <=  #{datetime2,jdbcType=BIGINT} 
		   
		
	

order by apply_add_date desc

SELECT b.bonus_extract_apply_id,b.user_basics_id,ui.information_compellation,ui.information_card,b.apply_add_date,b.apply_edit_date, b.edit_user_basics_id,b.apply_sum,apply_status,b.apply_postscript FROM bonus_extract_apply b LEFT JOIN user_information ui ON(b.user_basics_id=ui.user_basics_id)
ui.information_compellation LIKE concat('%',#{information_compellation,jdbcType=VARCHAR},'%')
AND ui.information_card = #{information_card,jdbcType=VARCHAR}
AND apply_status=#{apply_status,jdbcType=INTEGER}
AND apply_status =5

	   SELECT e.bonus_extract_apply_id,e.apply_add_date,e.apply_sum,e.apply_status,e.receive_bank_card,e.apply_postscript
       FROM user_basics b LEFT JOIN bonus_extract_apply e ON(b.user_basics_id=e.user_basics_id)
       WHERE b.user_basics_id=#{user_basics_id,jdbcType=INTEGER}
