SELECT ub1.user_nickname as superior_user_nickname,ub.user_nickname,ub.user_grade_id as user_grade_id, uw.weixin_portrait, uw.user_basics_id,uw.weixin_nickname,ur.recommend_superior, uw1.weixin_nickname as superior_weixin_nickname,ud.duty_num FROM user_recommend ur LEFT JOIN user_weixin uw ON(ur.user_basics_id=uw.user_basics_id) LEFT JOIN user_weixin uw1 ON(ur.recommend_superior=uw1.user_basics_id) LEFT JOIN user_basics ub ON(ur.user_basics_id=ub.user_basics_id) LEFT JOIN user_basics ub1 ON(ur.recommend_superior=ub1.user_basics_id) LEFT JOIN (select a.user_basics_id,count(*) as duty_num from user_duty a, user_recommend b WHERE b.recommend_superior in (
#{recommend_superior,jdbcType=INTEGER}
) and a.if_duty=0 group by user_basics_id ) ud on uw.user_basics_id=ud.user_basics_id WHERE ur.recommend_superior in (
#{recommend_superior,jdbcType=INTEGER}
