group by uw.user_basics_id

SELECT ob.order_id,max(ob.order_status) as order_status,ob.order_add_date,ob.store_picture,ob.all_price,ob.store_picture,ub1.user_nickname as superior_user_nickname,ub.user_nickname,uw.weixin_portrait, uw.user_basics_id,uw.weixin_nickname,ur.recommend_superior,uw1.weixin_nickname as superior_weixin_nickname FROM user_recommend ur LEFT JOIN user_weixin uw ON(ur.user_basics_id=uw.user_basics_id)LEFT JOIN user_weixin uw1 ON(ur.recommend_superior=uw1.user_basics_id) LEFT JOIN user_basics ub ON(ur.user_basics_id=ub.user_basics_id) LEFT JOIN user_basics ub1 ON(ur.recommend_superior=ub1.user_basics_id) LEFT JOIN order_basics ob ON(ur.user_basics_id=ob.user_basics_id) WHERE ur.recommend_superior in (
#{recommend_superior,jdbcType=INTEGER}
) group by user_basics_id

